import express from 'express';
import { json } from 'body-parser';
import 'reflect-metadata';
import DrugsController from "./service-layer/controllers/DrugsController";

const app = express();

app.use(json());

const drugsController = new DrugsController();

drugsController.mount(app);

export default app;

import { Request, Response } from 'express';
import AddDrugTask, { AddDrugData } from '../tasks/AddDrugTask';
import DeleteDrugTask from '../tasks/DeleteDrugTask';
import FindDrugTask from '../tasks/FindDrugTask';
import GetDrugListTask from '../tasks/GetDrugListTask';
import UpdateDrugTask, { UpdateDrugData } from '../tasks/UpdateDrugTask';
import BaseController from './BaseController';

export default class DrugsController extends BaseController {
  public constructor() {
    super('/drugs');
  }

  protected configureRouter(): void {
    this.router.get('/', this.getDrugsList.bind(this));
    this.router.get('/:id', this.findDrug.bind(this));
    this.router.post('/', this.addDrug.bind(this));
    this.router.put('/', this.updateDrug.bind(this));
    this.router.delete('/:id', this.deleteDrug.bind(this));
  }

  private async getDrugsList(req: Request, res: Response): Promise<void> {
    try {
      const getDrugListTask = new GetDrugListTask();

      const drugsList = await getDrugListTask.execute();

      this.respond(res, 200, drugsList);
    } catch (e) {
      this.respond(res, 500);
    }
  }

  private async findDrug(req: Request, res: Response): Promise<void> {
    try {
      const drugId = parseInt(req.params.id);
      const getDrugListTask = new FindDrugTask(drugId);

      const drug = await getDrugListTask.execute();

      this.respond(res, 200, drug);
    } catch (e) {
      if ((<Error>e).message === 'Drug not found.') {
        this.respond(res, 404);
      } else {
        this.respond(res, 500);
      }
    }
  }

  private async addDrug(req: Request, res: Response): Promise<void> {
    try{
      const drugData = <AddDrugData>req.body;

      const addDrugTask = new AddDrugTask(drugData);

      const drug = await addDrugTask.execute();

      this.respond(res, 200, drug);
    } catch (e) {
      this.respond(res, 500);
    }
  }

  private async updateDrug(req: Request, res: Response): Promise<void> {
    try {
      const drugData = <UpdateDrugData>req.body;

      const updateDrugTask = new UpdateDrugTask(drugData);

      const updateDrug = await updateDrugTask.execute();

      this.respond(res, 200, updateDrug);
    } catch (e) {
      if ((<Error>e).message === 'Drug not found.') {
        this.respond(res, 404);
      } else {
        this.respond(res, 500);
      }
    }
  }

  private async deleteDrug(req: Request, res: Response): Promise<void> {
    try{
      const drugId = parseInt(req.params.id);
      const deleteDrugTask = new DeleteDrugTask(drugId);

      await deleteDrugTask.execute();

      this.respond(res, 200);
    } catch (e) {
      this.respond(res, 500);
    }
  }
}

import Drug from '../../domain-layer/entities/Drug';
import DatabaseConnection from '../../persistence-layer/DatabaseConnection';
import IAsyncTask from './IAsyncTask';

export type AddDrugData = {
  name: string,
  laboratory: string,
  description: string,
  image: string,
};

export default class AddDrugTask implements IAsyncTask<Drug> {
  private addDrugData: AddDrugData;

  public constructor(addDrugData: AddDrugData) {
    this.addDrugData = addDrugData
  }

  public async execute(): Promise<Drug> {
    const databaseConnection = await DatabaseConnection.getInstance();
    const drugRepository = databaseConnection.getRepository(Drug);

    const drug = drugRepository.save(this.addDrugData);
    return drug;
  }
}

import { Entity, Column, PrimaryGeneratedColumn } from "typeorm";

@Entity()
export default class Drug {
  @PrimaryGeneratedColumn({ type: 'int', unsigned: true, zerofill: true})
  public id: number;

  @Column({ type: 'varchar', length: 16, nullable: false})
  public name: string;

  @Column({ type: 'varchar', length: 20,nullable: false})
  public laboratory: string;

  @Column({ type: 'varchar', length: 500, nullable: false})
  public description: string;

  @Column({ type: "varchar", length: 500, nullable: false})
  public image: string;

  public constructor(id: number, name: string, laboratory: string, description: string, image: string) {
    this.id = id;
    this.name = name;
    this.laboratory = laboratory;
    this.description = description;
    this.image = image;
  }
}

import { DataSource, EntityTarget, Repository } from "typeorm";
import Drug from "../domain-layer/entities/Drug";

export default class DatabaseConnection {
    private static instance: DatabaseConnection;

    private dataSource: DataSource;

    private constructor() {
        this.dataSource = new DataSource({
            type: 'mysql',
            host: 'localhost',
            port: 3306,
            username: 'root',
            password: '123456',
            database: 'drug_product',
            entities: [Drug],
            synchronize: true,
        });
    }

    public getRepository<Entity>(target: EntityTarget<Entity>): Repository<Entity> {
        return this.dataSource.getRepository(target);
    }

    public static async getInstance(): Promise<DatabaseConnection> {
        if(!DatabaseConnection.instance){
            DatabaseConnection.instance = new DatabaseConnection();
            await DatabaseConnection.instance.waitForInitialized();
        }
        return DatabaseConnection.instance;
    }

    private async waitForInitialized(): Promise<void> {
        await this.dataSource.initialize();
    }
}

import Drug from '../../domain-layer/entities/Drug';
import DatabaseConnection from '../../persistence-layer/DatabaseConnection';
import IAsyncTask from './IAsyncTask';

export default class DeleteDrugTask implements IAsyncTask<void> {
  private drugId: number;

  public constructor(drugId: number) {
    this.drugId = drugId;

  }

  public async execute(): Promise<void> {
    const databaseConnection = await DatabaseConnection.getInstance();
    const drugRepository = databaseConnection.getRepository(Drug);

    await drugRepository.delete(this.drugId);
  }
}

import Drug from '../../domain-layer/entities/Drug';
import DatabaseConnection from '../../persistence-layer/DatabaseConnection';
import IAsyncTask from './IAsyncTask';

export default class FindDrugTask implements IAsyncTask<Drug> {
  private drugId: number;

  public constructor(drugId: number) {
    this.drugId = drugId;
  }

  public async execute(): Promise<Drug> {
    const databaseConnection = await DatabaseConnection.getInstance();
    const drugRepository = databaseConnection.getRepository(Drug);

    const drug = await drugRepository.findOneBy({ id: this.drugId });

    if (!drug) {
      throw new Error('Drug not found.');
    }

    return drug;
  }
}

import Drug from '../../domain-layer/entities/Drug';
import DatabaseConnection from '../../persistence-layer/DatabaseConnection';
import IAsyncTask from './IAsyncTask';

export default class GetDrugListTask implements IAsyncTask<Drug[]> {
public async execute(): Promise<Drug[]> {
    const databaseConnection =  await DatabaseConnection.getInstance();
    const drugRepository = databaseConnection.getRepository(Drug);
    return drugRepository.find();
  }
}

import Drug from "../../domain-layer/entities/Drug";
import DatabaseConnection from "../../persistence-layer/DatabaseConnection";
import FindDrugTask from "./FindDrugTask";
import IAsyncTask from "./IAsyncTask";

export type UpdateDrugData = {
  id: number,
  name: string,
  laboratory: string,
  description: string,
  image: string,
};

export default class UpdateDrugTask implements IAsyncTask<Drug> {
  private updateDrugData: UpdateDrugData;

  public constructor(drugData: UpdateDrugData) {
    this.updateDrugData = drugData;
  }

  public async execute(): Promise<Drug> {
    const findDrugTask = new FindDrugTask(this.updateDrugData.id);

    const drug = await findDrugTask.execute();

    drug.name = this.updateDrugData.name;
    drug.laboratory = this.updateDrugData.laboratory
    drug.description = this.updateDrugData.description
    drug.image = this.updateDrugData.image

    const databaseConnection = await DatabaseConnection.getInstance();
    const drugRepository = databaseConnection.getRepository(Drug);

    drugRepository.save(drug);

    return drug;
  }
}

import { Application, Router, Response } from 'express';

export default abstract class BaseController {
  protected readonly path: string;

  protected readonly router: Router;

  public constructor(path: string) {
    this.path = path;
    this.router = Router();

    this.configureRouter();
  }

  protected abstract configureRouter(): void;

  public mount(app: Application): void {
    app.use(this.path, this.router);
  }

  public respond(res: Response, statusCode: number, data?: unknown): void {
    res.writeHead(statusCode, { 'Content-Type': 'application/json' });

    if(data) {
      res.end(JSON.stringify(data));
    } else{
      res.end()
    }
  }
}
